Ext.define('Roadpech.view.Login', {
    extend: 'Ext.Panel',
    alias: 'widget.loginView',

    config: {
        items: [
            {
                xtype: 'fieldset',
                title: 'Login',
                items: [
                    {
                        xtype: 'textfield',
                        itemId: 'username',
                        label: 'Username'
                    },
                    {
                        xtype: 'passwordfield',
                        itemId: 'password',
                        label: 'Password'
                    }
                ]
            },
            {
                xtype: 'button',
                itemId: 'logInButton',
                padding: '10px',
                ui: 'confirm',
                text: 'Login'
            },
            {
                xtype: 'button',
                itemId: 'signUpButton',
                padding: '10px',
                ui: 'action',
                text: 'Signup'
            }
        ],
        listeners: [
            {
                fn: 'onSignUpButtonTap',
                event: 'tap',
                delegate: '#signUpButton'
            },
            {
                fn: 'onLogInButtonTap',
                event: 'tap',
                delegate: '#logInButton'
            }
        ]
    },

    onLogInButtonTap: function(button, e, eOpts) {
        var me = this;
        var username = me.down('#username').getValue(),
            password = me.down('#password').getValue();
        this.fireEvent('signInCommand', me, username, password);

    },

    	
    onSignUpButtonTap: function(button, e, eOpts) {
        var me = this;
        var username = me.down('#username').getValue(),
            password = me.down('#password').getValue();
        this.fireEvent('signUpCommand', me, username, password);

    }
});
